import React, { useState } from "react";
import {
  Text,
  View,
  Button,
  TextInput,
  FlatList,
  ImageBackground,
  TouchableOpacity,
  SafeAreaView,
} from "react-native";

import { PRODUCTS, CART } from "../data/Dummy-Data";
import styles from "../assets/Styles";
import { render } from "react-dom";
import CartComponent from "../components/CartComponent";

export default function CartScreen({ navigation }) {
  // let name = "", finalPrice = "", price = "", shipping = "";

  // CART[0]
  // for (let i = 0; i < CART.length; i++) {
  //   name = CART[i].product_title;
  //   shipping = CART[i].shipping;
  //   price = CART[i].price;
  //   finalPrice = CART[i].price + CART[i].shipping;
  // }

  let sum = 0,
    price = 0,
    shipping = 0;
  for (let i = 0; i < CART.length; i++) {
    price += CART[i].price;
    shipping += CART[i].shipping;
    sum += price + shipping;
  }
  return (
    <View style={([styles.centerView], { width: "100%" })}>
      <View style={([styles.centerView], { width: "100%" })}>
        <Text style={styles.cartTitleText}>{CART[0].product_title}</Text>
      </View>
      <View style={([styles.centerView], { width: "100%" })}>
        <Text style={styles.cartTitleText}>{CART[1].product_title}</Text>
      </View>
      <View style={([styles.centerView], { width: "100%" })}>
        <Text style={styles.cartTitleText}>{CART[2].product_title}</Text>
      </View>
      <View>
        <Text style={styles.cartTitleText}>{CART[3].product_title}</Text>
      </View>
      <View>
        <Text style={styles.cartTitleText}>
          {"Your Total Payment " + sum + "$"}
        </Text>
      </View>
    </View>
  );
}
