import { TextInput, ScrollView, View, Text, Button } from "react-native";
import React, { createRef } from "react";
import { FormItem, submitForm, Form } from "react-native-form-component";
import { useState } from "react";
import styles from "../assets/Styles";
import UserDetails from "../models/UserDetails";

export default function PaymentScreen({ navigation }) {
  const [userFirstName, setUserFirstName] = useState("");
  const [userLasttName, setUserLasttName] = useState("");
  const [emailAddress, setEmailAddress] = useState(true);
  const [cellNumber, setCellNumber] = useState("");
  const [country, setCountry] = useState("");
  const [city, setCity] = useState("");
  const [address, setAddress] = useState("");
  const [cardHolderName, setCardHolderName] = useState("");
  const [idNumber, setIdNumber] = useState("");
  const [creditCardNumber, setCreditCardNumber] = useState("");
  const [creditCardExDate, setCreditCardExDate] = useState("");
  const [cvv, setCvv] = useState("");

  //   if (emailAddress == "") {
  //     setEmailAddress(false);
  //   } else {
  //     setEmailAddress(true);
  //   }


  //////////////////////
  return (
    <View style={styles.generalView}>
      <Text style={styles.cartTitleText}>Details and Paymnet</Text>
      
      <FormItem
        style={{ margin: 15 }}
        placeholder="Enter Your First Name"
        isRequired
        value={UserDetails.fName}
        onChangeText={(text) => setUserFirstName(text)}
        asterik
      ></FormItem>

      <FormItem
        style={{ margin: 15 }}
        placeholder="Enter Your Last Name"
        isRequired
        value={UserDetails.lName}
        onChangeText={(text) => setUserLasttName(text)}
        asterik
      ></FormItem>

        <FormItem
        style={{ margin: 15 }}
        placeholder="Enter Your Email"
        isRequired
        value={UserDetails.email}
        onChangeText={(text) => setEmailAddress(text)}
        asterik
      ></FormItem>

        <FormItem
        style={{ margin: 15 }}
        placeholder="Enter Your Phone Nunber"
        isRequired
        value={UserDetails.cellNumber}
        onChangeText={(cellNumber) => setCellNumber(cellNumber)}
        asterik
      ></FormItem>

        <FormItem
        style={{ margin: 15 }}
        placeholder="Enter Your Country "
        isRequired
        value={UserDetails.count}
        onChangeText={(text) => setCountry(text)}
        asterik
       ></FormItem>

        <FormItem
        style={{ margin: 15 }}
        placeholder="Enter Your City"
        isRequired
        value={UserDetails.city}
        onChangeText={(text) => setCity(text)}
        asterik
        ></FormItem>

        <FormItem
        style={{ margin: 15 }}
        placeholder="Enter Your Address"
        isRequired
        value={UserDetails.address}
        onChangeText={(text) => setAddress(text)}
        asterik
        ></FormItem>

        <FormItem
        style={{ margin: 15 }}
        placeholder="Enter Your Card Holder Name"
        isRequired
        value={UserDetails.card}
        onChangeText={(text) => setCardHolderName(text)}
        asterik
        ></FormItem>

        <FormItem
        style={{ margin: 15 }}
        placeholder="Enter Your ID Number"
        isRequired
        value={UserDetails.id}
        onChangeText={(idNumber) =>  setIdNumber(idNumber)}
        asterik
        ></FormItem>

        <FormItem
        style={{ margin: 15 }}
        placeholder="Enter Your Credit Card"
        isRequired
        value={UserDetails.creditCard}
        onChangeText={(text) => setCreditCardNumber(text)}
        asterik
         ></FormItem>

        <FormItem
        style={{ margin: 15 }}
        placeholder="Enter Your Email"
        isRequired
        value={UserDetails.email}
        onChangeText={(text) => setCreditCardExDatesetEmailAddress(text)}
        asterik
        ></FormItem>

        <FormItem     
        style={{ margin: 15 }}
        placeholder="Enter Your Email"
        isRequired
        value={UserDetails.email}
        onChangeText={(text) => setEmailAddress(text)}
        asterik
        ></FormItem>

    </View>
  );
}
