import React, {useEffect, useState, useContext} from 'react';
import {
  View,
  ScrollView,
  Image,
  Text,
  TouchableOpacity,
  Button,
} from "react-native";
import { PRODUCTS, CART } from "../data/Dummy-Data";
import styles from "../assets/Styles";
import { MaterialCommunityIcons as Icon } from "react-native-vector-icons";

//
import { getProduct } from "../data/Dummy-Data";
import { CartContext } from '../CartContext';
//

export default function ProductsScreen({ route, navigation }) {
  // const product_id = route.params.product_id;
  // const current_product = PRODUCTS.find((item) => item.id === product_id);
  const { productId } = route.params;
  let totalPtrice = productId.price + productId.shipping;

  //
  const { addItemToCart } = useContext(CartContext);
  const [product, setProduct] = useState({});

  useEffect(() => {
    setProduct(getProduct(productId));
  });

  function onAddToCart() {
    addItemToCart(product.id);
  }

  //

  return (
    <View style={[styles.productDetailsContainer, { width: "100%" }]}>
      <ScrollView>
        <View style={styles.container}>
          <Image
            source={{ uri: productId.productImage }}
            style={styles.productImg}
          ></Image>
          <Text style={{ fontSize: 20, fontWeight: "bold", padding: 10 }}>
            {productId.product_title}
          </Text>
        </View>
        <Text
          style={[styles.productDetailsText, { color: "white", marginTop: 5 }]}
        >
          Product's Description:
        </Text>
        <View style={[styles.viewDivProductDetails, styles.centerView]}>
          <Text style={styles.productDetailsText}>
            {productId.description_paragraph}
          </Text>
        </View>
        <Text
          style={[
            styles.productDetailsText,
            { color: "white", textAlign: "left", marginTop: 5 },
          ]}
        >
          Product's Size:
        </Text>
        <View style={[styles.viewDivProductDetails, { width: 160 }]}>
          <Text style={styles.productDetailsText}>
            {"Size: " + current_product.size}
          </Text>
        </View>
        <Text
          style={[
            styles.productDetailsText,
            { color: "white", textAlign: "left", marginTop: 5 },
          ]}
        >
          Product's Shipping Price:
        </Text>
        <View style={[styles.viewDivProductDetails, { width: 250 }]}>
          <Text style={styles.productDetailsText}>
            {"Shipping Price: " + productId.shipping + "$"}
          </Text>
        </View>
        <Text
          style={[
            styles.productDetailsText,
            { color: "white", textAlign: "left", marginTop: 5 },
          ]}
        >
          Product's Total Cost:
        </Text>
        <View style={[styles.viewDivProductDetails, { width: 225 }]}>
          <Text style={styles.productDetailsText}>
            {"Total Price: " + totalPtrice + "$"}
          </Text>
        </View>

        <View style={styles.centerView}>
          <TouchableOpacity
            onPress={ onAddToCart
              // console.log("On press = Cart Screen");
              // CART.push(current_product);
              
            }
          >
            <Text
              style={[
                styles.centerView,
                { color: "white", fontWeight: "bold", fontSize: 25 },
              ]}
            >
              Cart
            </Text>
            <Icon name="cart" size={55} color="white"></Icon>
          </TouchableOpacity>
        </View>

        <View style={[styles.centerView, { margin: 17 }]}>
          <TouchableOpacity
            onPress={() => {
              console.log("Navigate");
              navigation.navigate("Your Cart");
            }}
          >
            <Text
              style={[
                styles.centerView,
                { color: "white", fontWeight: "bold", margin: 15 },
              ]}
            >
              Go To The Cart
            </Text>
          </TouchableOpacity>
        </View>
      </ScrollView>
    </View>
  );
}

