import React from "react";
import styles from "../assets/Styles";
import { Text, ScrollView, TouchableOpacity } from "react-native";
import { Divider } from "react-native-elements";

export default function SummaryScreen({ navigation }) {
  const summary = "Your order has been placed!";

  return (
    <ScrollView style={styles.generalView}>
      <Text style={styles.finalTitle}>{summary}</Text>

      <Text style={[styles.finalTitle, {paddingTop: 150, color: "#E74C3C"}]}>Thank You ! ! !</Text>
      <TouchableOpacity
        style={[
          styles.touchableOpacityStyle,
          { marginLeft: "35%", backgroundColor: "#F7DC6F" },
        ]}
        onPress={() => navigation.navigate("Payment Summary")}
      >
        <Text style={styles.cartTitleText}>Continue </Text>
      </TouchableOpacity>
    </ScrollView>
  );
}
