            onPress={CART.bind(this, CART)}
          >
            <Text style={styles.productTitle}> {CART[0]} </Text>

            <Text style={styles.productTitle}> {CART[1]} </Text>

            <Text style={styles.productTitle}> {CART[2]} </Text>

            <View
              style={{ width: "100%", height: 1, backgroundColor: "#000" }}
            />
          </TouchableOpacity>
        ))}
      </ScrollView>
    </View>
  );
}

