import React, { useState } from "react";
import {
  Text,
  View,
  Button,
  TextInput,
  FlatList,
  ImageBackground,
  TouchableOpacity,
  SafeAreaView,
} from "react-native";

import { PRODUCTS, CART } from "../data/Dummy-Data";
import styles from "../assets/Styles";
import { render } from "react-dom";
import CartComponent from "../components/CartComponent";

export default function CartScreen({ navigation }) {
  let name = "",
    finalPrice = "";

  for (let i = 0; i < CART.length; i++) {
    name += CART[i].product_title;
    finalPrice += CART[i].price + CART[i].shipping;
  }

  return (
    
    <View style={[styles.productDetailsContainer], {width: "100%"} }>
      <Text style={styles.productTitle}>Cart</Text>
      <View>
        
        <Text style={styles.productTitle}>{"Product: " + name} {"\n"} {"Final Price: " + finalPrice + "$"}</Text>
      </View>
    </View>

