  return(
    <View>
    <Form onButtonPress={() => submitForm()}>
    <FormItem
    label="Email"
    isRequired
    value={UserDetails.email}
    onChangeText={(value) => setEmailAddress(value)}
    asterik
  />
  </Form>
    </View>
    

  )
}
