// app styles
// imports
import { StyleSheet } from "react-native";

export default StyleSheet.create({
  screen: {
    flex: 1,
    justifyContent: "center",
    alignContent: "center",
  },

  gridItem: {
    flex: 1,
    margin: 20,
    height: 370,
  },

  productGridItem: {
    flex: 1,
    margin: 25,
    height: 250,
  },

  container: {
    flex: 1,
    borderRadius: 0,
    shadowColor: "#CD6155",
    shadowOpacity: 3,
    shadowOffset: { width: 0, height: 2 },
    shadowRadius: 10,
    elevation: 3,
    justifyContent: "flex-end",
    alignItems: "flex-end",
  },

  background_image: {
    flex: 1,
    width: "100%",
    height: "100%",
    justifyContent: "center",
  },

  title: {
    fontSize: 50,
    color: "#FDFEFE",
    textAlign: "center",
    fontWeight: "bold",
    position: "center",
  },

  productTitle: {
    fontSize: 20,
    position: "relative",
    
    color: "black",
    textAlign: "left",
    fontWeight: "bold",
    position: "center",
  },
});
